  const [data, setData]=useState(data1);


  const handleSubmit=()=>{
    setSearch(data1.current.value);
  }

  const filter=(search)=>{
    if (search==='') setData(data1);
    else{
    const newarr=data1.filter((item)=>item.name.includes(search));
    setData(newarr);}
  }

