// import logo from './logo.svg';
import React from "react";
import codeSnip from "./codeSnip.js";

import data1 from "./data.json";
import {useState,useEffect,useRef} from "react";
import './App.css';

function App() {
  const[data, setData]=useState(data1);
  const[search, setSearch]=useState('');
  const field=useRef();


  const handleSubmit=()=>{
    setSearch(field.currentvalue);
  }
  const filter=(search)=>{
    if(search === ' ')
    setData(data1);
    else{
      const newarr=data1.filter((item)=>item.name.includes(search))
      setData(newarr);
    }
  }
  // const navigate=useNavigate("https://www.tabnine.com/code/javascript/functions/express/Express/listen");

  useEffect(()=>{
    filter(search);
  },[search])

 return (
   <>
   <div className="container">
     <input type="text" ref="field" placeholder= "search" />
     {/* <div>
       <a href="https://www.tabnine.com/code/javascript/functions/express/Express/listen"/>
     </div> */}

     <button type="submit" onClick={handleSubmit}>Submit</button>
     <div className="container1">
     {data.length===0 && <h4 style={{color: "red"}}>No matching results found</h4>}
     {data.length!==0 && data.map((item )=> 
      <codeSnip key={item.id} name={item.name} code={item.body}/>)}

     </div>
   </div>
  </>
  );
}

export default App;
