import { useEffect, useState } from 'react';
import './App.css';
import CodeSnip from './CodeSnip';
import data1 from './data.json';


function App() {
  const [search, setSearch]=useState ('');
  const [data, setData]=useState(data1);


  const handleSubmit=()=>{
    setSearch(field.current.value.toLowerCase());
  }

  const filter=(search)=>{
    if (search==='') setData(data1);
    else{
    const newarr=data1.filter((item)=>item.name.toLowerCase().includes(search));
    setData(newarr);}
  }

  useEffect(()=>{
    filter(search);
  }
  ,[search])

  return (
    <div class='container'>
      <input type="text"  placeholder="Search"/>
      <button type="submit" onClick={handleSubmit}>Submit</button>
    <div className="container1">

    {data.length!==0 && data.map((item )=> 
      <CodeSnip key={item.id} name={item.name} code={item.body}/>)}
    </div>
    </div>
  );
}

export default App;
